DO $$ BEGIN IF NOT EXISTS (SELECT 1 FROM pg_type WHERE typname='role_enum') THEN CREATE TYPE role_enum AS ENUM ('owner','manager','teacher','student'); END IF; IF NOT EXISTS (SELECT 1 FROM pg_type WHERE typname='section_enum') THEN CREATE TYPE section_enum AS ENUM ('reading','listening','speaking','writing'); END IF; IF NOT EXISTS (SELECT 1 FROM pg_type WHERE typname='note_type_enum') THEN CREATE TYPE note_type_enum AS ENUM ('listening_notes','reading_translation'); END IF; END $$ LANGUAGE plpgsql;